package pers.paopa.statictest;public class StaticFunctionTest {    public static void main(String[] args) {        NonStaticFunction x = new NonStaticFunction();        x.test();    }    public static void memoryUsage() {        System.out.println(Runtime.getRuntime().totalMemory() - Runtime.getRuntime().freeMemory());    }}class NonStaticFunction {    public void test() {        Runtime rt = Runtime.getRuntime();        long prevTotal = 0;        long prevFree = rt.freeMemory();        for (int i = 0; i < 2_000_000; i++) {            long total = rt.totalMemory();            long free = rt.freeMemory();            if (total != prevTotal || free != prevFree) {                System.out.println(                        String.format("#%s, Total: %s, Free: %s, Diff: %s",                                i,                                total,                                free,                                prevFree - free));                prevTotal = total;                prevFree = free;            }        }    }}